import React, { useState } from 'react';
import { Container, Row, Col, Button, ButtonGroup, Form} from 'react-bootstrap';
import ShowExpedients from './ShowExpedients';
import SaveExpedient from './SaveExpedient';

const Expedients = (props) => {
  const [vista, setVista] = useState(false);
  const [form, setForm] = useState(false);
  const [search , setSearch] = useState('');
  const [expedientes , setExpedientes] = useState([
   {id: 1, nombre: "Agua azul"},
   {id: 2, nombre: "LA VOLCADA DEL OESTE"}
  ]);

  const Buscar = e => {
    setSearch(...search, [e.target.value]);
  }

  return (
    <Container>
        <Row>
            <Col>
                <h4 className="text-primary font-weight-bold mb-3 ml-2">
                    Expedientes
                </h4>   
            </Col>
        </Row>
        <div className="shadow p-3 bg-white rounded mb-2">
        	<Row className="mb-3">
                <Col sm={4}>
                    <Form.Control
                    type="search"
                    name="search"
                    value={search}
                    onChange={Buscar}
                    placeholder="Buscar.." />
                </Col>
                <Col sm={8} className="text-right">
                    <ButtonGroup>
                      <Button onClick={() => setVista(true)} variant="default">Lista</Button>
                      <Button onClick={() => setVista(false)} variant="default">Cuadricular</Button>
                      <Button onClick={() => setVista(false)} variant="primary">
                        Nuevo Expediente
                      </Button>
                    </ButtonGroup>
                </Col>
        	</Row>
            <ShowExpedients expedientes={expedientes} vista={vista} />
        </div>
        <SaveExpedient />
    </Container>
  )
}

export default Expedients;
