import api from '../config/axios';
import { header } from '../config/headers';

export const getUsers = async () => {
	try {
		const resp = await api.get('/api/users', {headers: header()});
		return resp;
	} catch (error) {
		return error;
	}
}

export const saveUser = async data => {
	try {
		const resp = await api.post('/api/users/save', data ,{headers: header()});
		return resp;
	} catch (error) {
		return error;
	}
}

export const editUser = async data => {
	try {
		const resp = await api.put('/api/users/edit', data ,{headers: header()});
		return resp;
	} catch (error) {
		return error;
	}
}

export const resetPassword = async data => {
	try {
		const resp = await api.put('/api/users/resetpassword', data ,{headers: header()});
		return resp;
	} catch (error) {
		return error;
	}
}

export const deleteUser = async data => {
	try {
		const resp = await api.delete('/api/users/delete/'+data.id , {headers: header()});
		return resp;
	} catch (error) {
		return error;
