import React, { useState, useContext } from 'react';
import { useForm } from "react-hook-form";

import { Form, Button, Alert } from 'react-bootstrap';
import { onLogin } from '../../services/auth';
import AppContext from '../../context/appContext';
import logo from '../../resource/logo.png';

const FormLogin = (props) => {
	const { register, handleSubmit, errors } = useForm();
	const [ message, setMessage ] = useState('');
	const [ loading, setLoading ] = useState(false);
	const { setLogin } = useContext(AppContext);
	
	const Submit = async data => {
		setLoading(true);
		const resp = await onLogin(data);
		if (resp.data)
			setLogin(resp.data);
		else
			setMessage(resp.response ? resp.response.data.error : "Error de conexion");
		setLoading(false);
	}

  return (
  	<React.Fragment>
  		<img src={logo} alt="" className="mx-auto mt-2 mb-2 d-block" width="100px" />
    	<Form onSubmit={handleSubmit(Submit)} >
    		<Form.Group controlId="formBasicEmail">
			    <Form.Label>Usuario</Form.Label>
			    <Form.Control 
			    type="text"
			    disabled={loading}  
			    ref={register({ required: true })}
			    name="username" 
			    isInvalid={errors.username}
			    placeholder="Ingresar usuario" />
			    <Form.Text className="text-danger">
			      {errors.username && "Campo incorrecto, favor de validarlo."}
			    </Form.Text>
			  </Form.Group>

			  <Form.Group controlId="formBasicPassword">
			    <Form.Label>Contraseña</Form.Label>
			    <Form.Control 
			    type="password"
			    disabled={loading}  
			    ref={register({ required: true })}
			    name="userpassword"
			    isInvalid={errors.userpassword} 
			    placeholder="Ingresar contraseña" />
			    <Form.Text className="text-danger">
			      {errors.userpassword && "Campo incorrecto, favor de validarlo."}
			    </Form.Text>
			  </Form.Group>

			  <Button
			  disabled={loading} 
			  className="text-uppercase w-100 mt-3" 
			  variant="primary" 
			  type="submit">
			    {loading ? 'Cargando..' : 'Ingresar'}
			  </Button>
    	</Form>
    	{message ? (<Alert variant="danger" className="mt-3">{message}</Alert>) : null}
    </React.Fragment>
  )
}

export default FormLogin;
