import React, { useContext, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import AppContext from '../../context/appContext';
import './menu.css';
import Perfil from './Perfil';

const MenuBar = (props) => {
    const { token } = useContext(AppContext);
    const mostrar = "";
    
    useEffect( () => {
      if ( !props.showMenu )
        mostrar = "d-none";
      else
        mostrar = "d-inline-block";
    }, [mostrar]);
    
    if (!token) return null;
  return (
    <div className={`menu-bar flex-nowrap ${mostrar}`}>
        <Perfil />
    	<div className="menu">
    		<NavLink to="/home" activeClassName="active">Inicio</NavLink>
    		<NavLink to="/users" activeClassName="active">Usuarios</NavLink>
    		<NavLink to="/expedients" activeClassName="active">Expedientes</NavLink>
    		<NavLink to="/formats" activeClassName="active">Formatos</NavLink>
    	</div>
    </div>
  )
}

export default MenuBar;
