import React, { useState } from 'react';
import { Table, Badge, Form, Col, Row } from 'react-bootstrap';

const ShowUsers = props => {
  const tableHeader = ["#", "Nombre", "Apellidos", "Usuario", "Estado"];

  const SelectUser = data => {
    props.setUser(data);
  }

  return (
	    <Table striped bordered hover responsive="md">
			<thead>
		      <tr>
		        {tableHeader.map((header, i) => (
		        	<th key={i}>{header}</th>
		        ))}
		      </tr>
		    </thead>
		    <tbody>
		      {props.users.filter(user => {
            return Object.keys(user).some(key =>
              user[key].toString().toLowerCase().includes(props.search.buscar.toLowerCase()))
          }).map(user => (
    				<tr key={user.id} onClick={() => SelectUser(user)}>
    					<td>{user.id}</td>
    					<td>{user.name}</td>
    					<td>{user.app}</td>
    					<td>{user.username}</td>
    					<td className="text-center" >
    						<Badge pill
    						variant={user.status ? 'success' : 'danger'}>
    							{user.status ? 'Activo' : 'Inactivo'}
    						</Badge>
    					</td>
    				</tr>
		      ))}
		    </tbody>
		</Table>
  )
}

export default ShowUsers;
