import React, {useState, useEffect} from 'react';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';
import { getUsers } from '../../services/users';
import ShowUsers from './ShowUsers';
import SaveUser from './SaveUser';
import EditUser from './EditUser';

const Users = (props) => {
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState({});
  const [form, setForm] = useState(false);
  const [search, setSearch] = useState({
    buscar: ""
  });

  const Buscar = e => {
    setSearch({
      ...search,
      buscar: e.target.value
    });
  }

  const getData = async () => {
    const resp = await getUsers();
    setUsers(resp.data);
  }

  useEffect(() => {
    getData();
  }, []);

  return (
    <Container>
    <Row>
      <Col>
        <h4 className="text-primary font-weight-bold mb-3">
          USUARIOS
        </h4>        
      </Col>
    </Row>
      <div className="shadow p-4 mb-5 bg-white rounded">
        <Row>
          <Col sm="4">
            <Form.Control
            type="search"
            name="buscar"
            className="mb-2"
            value={search.buscar}
            onChange={Buscar}
            placeholder="Buscar.." />
          </Col>
          <Col sm="8">
            <Button variant="outline-primary" onClick={setForm}>
              Agregar
            </Button>
          </Col>
        </Row>
      	<Row>
      		<Col>
              <ShowUsers users={users} setUser={setUser} search={search} />
      		</Col>
      	</Row>
      </div>

      <EditUser user={user} setUser={setUser} getData={getData} />
      <SaveUser getData={getData} form={form} setForm={setForm} />
    </Container>
  )
}

export default Users;
