import React, { useContext, useState, useEffect } from 'react';
import { Navbar, Button } from 'react-bootstrap';
import logo from '../../resource/logo-white.png';
import AppContext from '../../context/appContext';
import MenuBar from './MenuBar';
import Routes from '../../Routes';
import './menu.css';

const Bar = (props) => {
	const { token } = useContext(AppContext);
	const [showMenu, setShowMenu] = useState(true);
	const [showBtn, setShowBtn] = useState(false);

	useEffect( () => {
		if (window.innerWidth <= 950){
			setShowBtn(true);
			setShowMenu(false);
		}
	}, []);

	window.addEventListener("resize", () => {
		if (window.innerWidth <= 950){
			setShowBtn(true);
			setShowMenu(false);
		}
		else{
			setShowBtn(false);
			setShowMenu(true);
		}
	});

	return (
		<React.Fragment>
			{showMenu ? (<div className="shadow-div" />) : null}
			<Navbar bg="primary" className="pr-3 pl-3 pt-3 pb-3" variant="dark">
				<Navbar.Brand className="w-100 text-center">
					{showBtn ? (
					<Button 
					onClick={() => setShowMenu(!showMenu)}
					variant="outline-light" 
					className="btn-menu mr-3">
						Menu
					</Button>
					) : null}
					<img alt="" src={logo} width="30" height="30"
					className="d-inline-block align-top mr-3" />
					<b className="text-uppercase flex-nowrap">
						ACE Soluciones y Proyectos Inmobiliarios
					</b>
				</Navbar.Brand>
			</Navbar>
			<MenuBar showMenu={showMenu} setShowMenu={setShowMenu} />
			<div className="main">
				<Routes />
			</div>
		</React.Fragment>
	)
}

export default Bar;
