import React, {useState} from 'react';
import { Dialog, DialogTitle, DialogContent, TextField, List, ListItem } from '@material-ui/core';
import Style from './styles';
import { Buscar } from '../../services/api_clima';

const Modal = props => {
	const classes = Style();

	const [search, setSearch] = useState({});

	const Changer = async e => {
		const quest = e.target.value;
		const respuesta = await Buscar(quest);
		if (respuesta.status === 200)
			setSearch(respuesta.data);
		else{
			search({});
			console.log("No se encuentra el dato filtrado.");
		}
	}

	return (
		<Dialog onClose={() => props.setDialog(false)} open={props.dialog}>
	      <DialogTitle>
	      	Agregar Nueva Ubicacion
	      </DialogTitle>
	      <DialogContent dividers>
	      	<div className={classes.modal}>
	      		<TextField 
	      		className={classes.input} 
	      		type="search"
	      		name="buscar"
	      		onChange={Changer}
	      		label="Buscador..." />
		      	<List>
			       {search.length > 0 ? (
			       	<ListItem>Mexico</ListItem>
			       ) : null}
		       	</List>
	      	</div>
	      </DialogContent>
	    </Dialog>
	);
}

export default Modal;
