import api from '../config/axios';
import { header } from '../config/headers';

export const getClimas = async () => {
	try {
		const resp = await api.get('/api/climas', {headers: header()});
		return resp;
	} catch (error) {
		return error;
	}
}

export const saveClima = async data => {
	try {
		const resp = await api.post('/api/climas', data , {headers: header()});
		return resp;
	} catch (error) {
		return error;
	}
}

export const editClima = async data => {
	try {
		const resp = await api.put('/api/climas/edit', data ,{headers: header()});
		return resp;
	} catch (error) {
		return error;
	}
}

