import React, {useState} from 'react';
import { Dialog, DialogTitle, DialogContent, TextField, List, ListItem, ListItemIcon, ListItemText, Typography } from '@material-ui/core';
import Style from './styles';
import { Buscar } from '../../services/api_clima';
import LocationOnIcon from '@material-ui/icons/LocationOn';

const Modal = props => {
	const classes = Style();

	const [search, setSearch] = useState(null);

	const Changer = async e => {
		const quest = e.target.value;
		const respuesta = await Buscar(quest);
		if (respuesta.status === 200)
			setSearch(respuesta.data);
		else{
			setSearch(null);
			console.log("No se encuentra el dato filtrado.");
		}
	}

	return (
		<Dialog onClose={() => props.setDialog(false)} open={props.dialog}>
	      <DialogTitle className={classes.modalTitle}>
	      	Agregar Nueva Ubicacion
	      </DialogTitle>
	      <DialogContent dividers>
	      	<div className={classes.modal}>
	      		<TextField 
	      		className={classes.input} 
	      		type="text"
	      		name="buscar"
	      		onChange={Changer}
	      		variant="outlined"
	      		label="Buscador..." />
		      	
		      	<Typography className={classes.letras} variant="h6" component="h2">
		      		Resultados de la busqueda:
		      	</Typography>

		      	<List>
			       {search !== null ? (
			       	<ListItem button>
			       		<ListItemIcon>
			       			<LocationOnIcon />
			       		</ListItemIcon>
			       		<ListItemText primary={search.name+' - '+search.sys.country} />
			       	</ListItem>
			       ) : null}
		       	</List>

	      	</div>
	      </DialogContent>
	    </Dialog>
	);
}

export default Modal;
