import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  root: {
    '& > *': {
      marginTop: theme.spacing(1),
      marginBottom: theme.spacing(1),
      width: '100%',
    },
  },
  login: {
  	width: '100%',
  	maxWidth: 400,
  	padding: theme.spacing(1),
  	display: 'block',
  	margin: '50px auto'
  },
  loading: {
  	display: 'block',
  	marginTop: '20px',
  	textAlign: 'center'
  }
}))
