import React, { useState } from 'react';
import { Table, Badge, Form, Col, Row } from 'react-bootstrap';

const ShowUsers = props => {
  const tableHeader = ["#", "Nombre", "Apellidos", "Usuario", "Estado"];
  const [search, setSearch] = useState({
    buscar: ""
  });

  const Buscar = e => {
  	setSearch({
      ...search,
  		buscar: e.target.value
    });
  }

  const SelectUser = data => {
    props.setUser(data);
  }

  return (
  	<React.Fragment>
      <Row className="justify-content-end">
    		<Col sm="4">
  	  		<Form.Control
  	  		type="search"
          name="buscar"
          className="mb-2"
  	  		value={search.buscar}
  	  		onChange={Buscar}
  	  		placeholder="Buscar.." />
  	  	</Col>
      </Row>
	    <Table striped bordered hover responsive="md">
			<thead>
		      <tr>
		        {tableHeader.map((header, i) => (
		        	<th key={i}>{header}</th>
		        ))}
		      </tr>
		    </thead>
		    <tbody>
		      {props.users.filter(user => {
            return Object.keys(user).some(key =>
              user[key].toString().toLowerCase().includes(search.buscar.toLowerCase()))
          }).map(user => (
    				<tr key={user.id} onClick={() => SelectUser(user)}>
    					<td>{user.id}</td>
    					<td>{user.name}</td>
    					<td>{user.app}</td>
    					<td>{user.username}</td>
    					<td className="text-center" >
    						<Badge pill
    						variant={user.status ? 'success' : 'danger'}>
    							{user.status ? 'Activo' : 'Inactivo'}
    						</Badge>
    					</td>
    				</tr>
		      ))}
		    </tbody>
		</Table>
	</React.Fragment>
  )
}

export default ShowUsers;
