import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import AppContext from '../../context/appContext';
import { Container, Row, Col, Image, Button } from 'react-bootstrap';
import imagen from '../../resource/ingeniero.png';

const Perfil = () => {
	const { user } = useContext(AppContext);
	const { name, app } = user;

	return(
		<Container className="mb-3">
			<Row>
				<Col sm={12}>
					<Image src={imagen} className="w-50 d-block ml-auto mr-auto mt-3" roundedCircle />
				</Col>
				<Col sm={12}>
					<small>Bienvenido:</small> 
					<Link to="/perfil" className="d-block text-wrap text-uppercase">
						<b>{name} {app}</b>
					</Link>
					<Button>
						Cerrar Sesi√≥n
					</Button>
				</Col>
			</Row>
		</Container>
	)
}

export default Perfil;
