import React, { useState, useEffect } from 'react';
import Navbar from '../navbar';
import { Grid, Card, CardContent, Typography, Link } from '@material-ui/core';
import ShowClimas from './showClimas';
import Styles from './styles';
import { Buscar } from '../../services/api_clima';
import AddCircleOutlinedIcon from '@material-ui/icons/AddCircleOutlined';
import Modal from './modal';

const Home = props => {
	const classes = Styles();
	const [climas, setClimas] = useState([]);
	const [dialog, setDialog] = useState(false);

	const showForm = e => {
		e.preventDefault();
		setDialog(true);
	}

	useEffect(() => {
		const consultar = async() => {
			const datos = await Buscar('tuxtla gutierrez');
			if (datos.status === 200)
				setClimas(datos.data);
			else
				console.log("No se encontro la ciudad");
			console.log(datos.data);
		}
	}, []);

    return(
        <div>
            <Navbar />
            <Grid
            className={classes.root}
			container
			direction="row"
			justify="center"
			alignItems="center">
			
				<ShowClimas climas={climas} />

				<Grid item sm={4}>
					<Card className={classes.card}>
						<CardContent className={classes.content}>
							<Link href="#" onClick={showForm} className={classes.registro}>
								<AddCircleOutlinedIcon className={classes.iconreg} />
								<Typography className={classes.labelreg} variant='h6' component="span">
									Agregar Nueva Ubicacion
								</Typography>
							</Link>
						</CardContent>
					</Card>
				</Grid>
			</Grid>
			
			<Modal dialog={dialog} setDialog={setDialog} />
        </div>
    );
}

export default Home;
