import React, { useState } from 'react';
import { useForm } from "react-hook-form";
import { Modal, Button, Form, Col, Alert } from 'react-bootstrap';
import { editUser, deleteUser } from '../../services/users';

const EditUser = props => {
  const { register, handleSubmit, errors } = useForm();
  const [ loading, setLoading ] = useState(false);
  const [ alert, setAlert ] = useState({
    message: "",
    type: ""
  });
  const { name, app, username, status, id } = props.user;

  const Submit = async data => {
    setLoading(true);
    const resp = await editUser(data);
    if (resp.status === 201){
        props.setUser({});
        props.getData();
    }
    else
      setAlert({
        message: resp.response ? resp.response.data.error : 'Error de conexion',
        type: "danger"
      });

    setLoading(false);
  }

  const Delete = async () => {
    const resp = await deleteUser(props.user);
    if (resp.status === 200){
      props.setUser({});
      props.getData();
    }
    else
      setAlert({
        message: resp.response ? resp.response.data.error : 'Error de conexion',
        type: "danger"
      });
  }

  return (
    <Modal show={props.user.id > 0 ? true : false} animation={false} onHide={() => props.setUser({})}>
      <Modal.Header closeButton>
        <Modal.Title className="text-primary">
          Editar: <b>{username}</b>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {alert.message ? (<Alert variant={alert.type} className="mt-3">{alert.message}</Alert>) : null}
        <Form onSubmit={handleSubmit(Submit)} >
          <input
            type="hidden"
            name="id"
            defaultValue={id}
            ref={register({ required: true })} />
          <Form.Row>
            <Form.Group as={Col} sm={12}>
              <Form.Label>Nombre</Form.Label>
              <Form.Control
              type="text"
              disabled={loading}
              defaultValue={name}
              ref={register({ required: true })}
              name="name"
              isInvalid={errors.name}
              placeholder="Ingresar Nombre" />
              <Form.Text className="text-danger">
                {errors.name && "Campo incorrecto, favor de validarlo."}
              </Form.Text>
            </Form.Group>

            <Form.Group as={Col} sm={12}>
              <Form.Label>Apellidos</Form.Label>
              <Form.Control
              type="text"
              disabled={loading}
              defaultValue={app}
              ref={register({ required: true })}
              name="app"
              isInvalid={errors.app}
              placeholder="Ingresar Apellidos" />
              <Form.Text className="text-danger">
                {errors.app && "Campo incorrecto, favor de validarlo."}
              </Form.Text>
            </Form.Group>

              <Form.Group as={Col} sm={6}>
              <Form.Label>Contraseña(Resetear)</Form.Label>
              <Form.Control
              type="password"
              ref={register}
              name="userpassword"
              placeholder="Ingresar Contraseña" />
            </Form.Group>

            <Form.Group as={Col} sm={6}>
              <Form.Label>Estado</Form.Label>
              <Form.Control
              as="select"
              defaultValue={status}
              disabled={loading}
              ref={register({ required: true })}
              name="status">
                <option value="1">Activo</option>
                <option value="0">Inactivo</option>
              </Form.Control>
            </Form.Group>
            <Form.Group as={Col} sm={6}>
              <Button
              disabled={loading}
              variant="primary"
              block
              className="mt-2"
              type="submit">
                  {loading ? 'Cargando..' : 'Guardar Cambios'}
              </Button>
            </Form.Group>
            <Form.Group as={Col} sm={6}>
              <Button
              disabled={loading}
              block
              className="mt-2"
              variant="outline-danger"
              onClick={Delete}>
                Eliminar Usuario
              </Button>
            </Form.Group>
          </Form.Row>
        </Form>
      </Modal.Body>
    </Modal>
  )
}

export default EditUser;
