import React, { useState, useContext } from 'react';
import { useForm } from "react-hook-form";

import { TextField, Card, CardContent, Button, CircularProgress} from '@material-ui/core';
import useStyles from './styles';
import { onLogin } from '../../services/auth';
import AppContext from '../../context/appContext';

const FormLogin = (props) => {
	const { register, handleSubmit, errors } = useForm();
	const [ message, setMessage ] = useState('');
	const [ loading, setLoading ] = useState(false);
	const { setLogin } = useContext(AppContext);
	const classes = useStyles();
	
	const Submit = async data => {
		setLoading(true);
		const resp = await onLogin(data);
		if (resp.data)
			setLogin(resp.data);
		else
			setMessage(resp.response ? resp.response.data.error : "Error de conexion");
		setLoading(false);
	}

  return (
  	<Card className={classes.login} elevation={3}>
  		<CardContent>
	  		<form className={classes.root} onSubmit={handleSubmit(Submit)}>
		      <TextField 
		      label="Correo Electronico" 
		      name="email" 
		      type="email"
		      disabled={loading} 
		      inputRef={register({ required: true })}
		      variant="outlined" />
		      
		      <TextField 
		      label="ContraseÃ±a" 
		      name="password" 
		      type="password"
		      disabled={loading} 
		      inputRef={register({ required: true, maxLength: 20 })}
		      variant="outlined" />
		      
		      <Button 
		      type="submit" 
		      variant="contained" 
		      disabled={loading}
		      color="primary">
		      	{loading ? 'Cargando..' : 'Ingresar'}
		      </Button>
		    </form>
	    	{message}
	    </CardContent> 
  	</Card>
  )
}

export default FormLogin;
