import React, { useState, useContext } from 'react';
import { useForm } from "react-hook-form";

import { TextField, Card, CardContent, Button, CircularProgress, Typography, Grid} from '@material-ui/core';
import useStyles from './styles';
import { onLogin } from '../../services/auth';
import AppContext from '../../context/appContext';

const FormLogin = (props) => {
	const { register, handleSubmit, errors } = useForm();
	const [ message, setMessage ] = useState('');
	const [ loading, setLoading ] = useState(false);
	const { setLogin } = useContext(AppContext);
	const classes = useStyles();
	
	const Submit = async data => {
		setLoading(true);
		/*
		const resp = await onLogin(data);
		if (resp.data)
			setLogin(resp.data);
		else
			setMessage(resp.response ? resp.response.data.error : "Error de conexion");
		setLoading(false);
		*/
	}

  return (
  	<Card className={classes.login} elevation={3}>
  		<CardContent>
  			<Typography variant="h5" className={classes.titulo} component="h2">
  				Iniciar Sesión
  			</Typography>
  			<Typography variant="body2" className={classes.subtitulo} component="h2">
  				Ingresar tu correo y contraseña
  			</Typography>
	  		<form className={classes.root} onSubmit={handleSubmit(Submit)}>
		      <TextField 
		      label="Correo Electronico" 
		      name="email" 
		      type="email"
		      disabled={loading} 
		      inputRef={register({ required: true, type: 'email' })}
		      variant="outlined" />
		      {errors.email && "Corre Invalido"}
		      
		      <TextField 
		      label="Contraseña" 
		      name="password" 
		      type="password"
		      disabled={loading} 
		      inputRef={register({ required: true, min: 6 })}
		      variant="outlined" />
		      {errors.password && "Contraseña Invalida"}
		     
		    <Grid container>
		    	<Grid item sm={6}>
		    		<Button 
			      	type="button" 
			      	disabled={loading}
			      	color="primary">
			      		Crear Cuenta
			      	</Button>
		    	</Grid>
		    	<Grid item sm={6} className={classes.button}>
		    		 {loading ? (
				    	<CircularProgress /> 
				    ) : (
				    	<Button 
				      	type="submit" 
				      	variant="contained" 
				      	disabled={loading}
				      	color="primary">
				      		Ingresar
				      	</Button>
				    )}
		    	</Grid>
		    </Grid>
		    
		    </form>
	    	{message}
	    </CardContent> 
  	</Card>
  )
}

export default FormLogin;
