import React, { useState } from 'react';
import { useForm } from "react-hook-form";
import { Form, Button, Alert, Col, Modal } from 'react-bootstrap';
import { saveUser } from '../../services/users';

const SaveUser = (props) => {
	const { register, handleSubmit, errors, reset } = useForm();
	const [ alert, setAlert ] = useState({
		text: "",
		type: ""
	});
	const [ loading, setLoading ] = useState(false);

	const Submit = async data => {
		setLoading(true);
		const resp = await saveUser(data);
		if (resp.data !== undefined){
			setAlert({ message: resp.data.message, type: "success"});
			props.getData();
			reset();
		}
		else
			setAlert({message: resp.response ? resp.response.data.error : "Error de conexion", type: "danger"});
		setLoading(false);
	}

  return (
    <Modal show={props.form} animation={false} onHide={() => props.setForm(false)}>
   		<Modal.Header closeButton>
	        <Modal.Title className="text-primary">
	          Nuevo Usuario
	        </Modal.Title>
	    </Modal.Header>
	    <Modal.Body>
	    	{alert.message ? (<Alert variant={alert.type} className="mt-3">{alert.message}</Alert>) : null}
	    	<Form onSubmit={handleSubmit(Submit)} >
	    	   <Form.Row>
	    		  <Form.Group as={Col} sm={12}>
				    <Form.Label>Nombre</Form.Label>
				    <Form.Control
				    type="text"
				    disabled={loading}
				    ref={register({ required: true })}
				    name="name"
				    isInvalid={errors.name}
				    placeholder="Ingresar Nombre" />
				    <Form.Text className="text-danger">
				      {errors.name && "Campo incorrecto, favor de validarlo."}
				    </Form.Text>
				  </Form.Group>

				  <Form.Group as={Col} sm={12}>
				    <Form.Label>Apellidos</Form.Label>
				    <Form.Control
				    type="text"
				    disabled={loading}
				    ref={register({ required: true })}
				    name="app"
				    isInvalid={errors.app}
				    placeholder="Ingresar Apellidos" />
				    <Form.Text className="text-danger">
				      {errors.app && "Campo incorrecto, favor de validarlo."}
				    </Form.Text>
				  </Form.Group>

	    		  <Form.Group as={Col}>
				    <Form.Label>Usuario</Form.Label>
				    <Form.Control
				    type="text"
				    disabled={loading}
				    ref={register({ required: true })}
				    name="username"
				    isInvalid={errors.username}
				    placeholder="Ingresar usuario" />
				    <Form.Text className="text-danger">
				      {errors.username && "Campo incorrecto, favor de validarlo."}
				    </Form.Text>
				  </Form.Group>

				  <Form.Group as={Col}>
				    <Form.Label>Contraseña</Form.Label>
				    <Form.Control
				    disabled={loading}
				    type="password"
				    ref={register({ required: true })}
				    name="userpassword"
				    isInvalid={errors.userpassword}
				    placeholder="Ingresar contraseña" />
				    <Form.Text className="text-danger">
				      {errors.userpassword && "Campo incorrecto, favor de validarlo."}
				    </Form.Text>
				  </Form.Group>
				</Form.Row>

				  <Button
				  disabled={loading}
				  className="text-uppercase mt-2" 
				  variant="primary"
				  type="submit">
				    {loading ? 'Cargando..' : 'Guardar'}
				  </Button>
	    	</Form>
    	</Modal.Body>
    </Modal>
  )
}

export default SaveUser;
