import React, {useState} from 'react';
import { Dialog, DialogTitle, DialogContent, TextField, List, ListItem, ListSubheader } from '@material-ui/core';
import Style from './styles';
import { Buscar } from '../../services/api_clima';

const Modal = props => {
	const classes = Style();

	const [search, setSearch] = useState(null);

	const Changer = async e => {
		const quest = e.target.value;
		const respuesta = await Buscar(quest);
		if (respuesta.status === 200)
			setSearch(respuesta.data);
		else{
			setSearch(null);
			console.log("No se encuentra el dato filtrado.");
		}
	}

	return (
		<Dialog onClose={() => props.setDialog(false)} open={props.dialog}>
	      <DialogTitle>
	      	Agregar Nueva Ubicacion
	      </DialogTitle>
	      <DialogContent dividers>
	      	<div className={classes.modal}>
	      		<TextField 
	      		className={classes.input} 
	      		type="search"
	      		name="buscar"
	      		onChange={Changer}
	      		label="Buscador..." />
		      	
		      	<List
		      	className={classes.lista} 
		      	 subheader={
			        <ListSubheader component="div">
			          Resultados de la busqueda:
			        </ListSubheader>
			      }>
			       {search !== null ? (
			       	<ListItem button>
			       		{search.name} - {search.sys.country}
			       	</ListItem>
			       ) : null}
		       	</List>

	      	</div>
	      </DialogContent>
	    </Dialog>
	);
}

export default Modal;
