import React, {useState, useEffect} from 'react';
import { Container, Row, Col, Tabs, Tab } from 'react-bootstrap';
import { getUsers } from '../../services/users';
import ShowUsers from './ShowUsers';
import SaveUser from './SaveUser';
import EditUser from './EditUser';

const Users = (props) => {
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState({});

  const getData = async () => {
    const resp = await getUsers();
    setUsers(resp.data);
  }

  useEffect(() => {
    getData();
  }, []);

  return (
    <Container>
     <EditUser user={user} setUser={setUser} getData={getData} />
    	<Row>
    		<Col>
          <div className="shadow p-4 mb-5 bg-white rounded">
          <h2 className="text-primary">Usuarios</h2>
            <Tabs defaultActiveKey="show" transition={false} className="mb-3">
              <Tab eventKey="show" title="Usuarios">
                <ShowUsers users={users} setUser={setUser} />
              </Tab>
              <Tab eventKey="new" title="Nuevo Usuario">
                <SaveUser getData={getData} />
              </Tab>
            </Tabs>
          </div>
    		</Col>
    	</Row>
    </Container>
  )
}

export default Users;
