import React, { useContext, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import AppContext from '../../context/appContext';
import './menu.css';
import Perfil from './Perfil';

const MenuBar = (props) => {
    const { token } = useContext(AppContext);
    let mostrar = '';
    
    if ( props.showMenu )
        mostrar = "menu-bar-show";
    
    const onHidden = () => {
      if (!mostrar)
        mostrar = "";
    }

    if (!token) return null;
    return (
      <div className={`menu-bar flex-nowrap ${mostrar}`}>
          <Perfil />
          <div className="menu">
            <NavLink to="/home" onClick={onHidden} activeClassName="active">Inicio</NavLink>
            <NavLink to="/users" onClick={onHidden} activeClassName="active">Usuarios</NavLink>
            <NavLink to="/expedients" onClick={onHidden} activeClassName="active">Expedientes</NavLink>
            <NavLink to="/formats" onClick={onHidden} activeClassName="active">Formatos</NavLink>
          </div>
      </div>
    )
}

export default MenuBar;
